export default function HomeScreen(): ReactElement {
  const { loginWithRedirect } = useAuth0()

  return (
    <div className="min-h-screen bg-black">
      <FancyBackground theme="blue" />

      <div className="relative m-auto flex max-w-screen-md flex-col items-center px-5 pb-12 pt-10 sm:pt-20">
        <Logo />
        <div className="relative mb-5 mt-7 aspect-square w-full bg-slate-300 sm:mb-10 sm:mt-14 sm:w-[500px]">
          <Image src="/media/images/girl-vibing.png" alt="" fill />
        </div>
        <Text
          text="Share in the Success of Your Favourite Creators"
          variant="title1"
          color="white"
          align="center"
        />
        <div className="mt-4 sm:px-20">
          <Text
            text="Empowers creators and drives growth, fostering a vibrant and collaborative ecosystem. Amplifying creative voices and enable creators to thrive in the evolving digital landscape"
            align="center"
            color="white"
            variant="body2"
          />
        </div>
        <div className="mt-10 grid w-full gap-4 sm:mt-12 sm:flex sm:flex-row sm:gap-8">
          <Button
            text="Sign up for Free"
            inline={false}
            onPress={() => {
              return loginWithRedirect({
                authorizationParams: {
                  screen_hint: 'signup',
                },
                appState: {
                  accountType: 'investor',
                  returnTo: $path({ route: '/campaigns' }),
                },
              })
            }}
          />
          <Button
            text="Log in to your Account"
            variant="tertiary"
            inline={false}
            onPress={() => {
              return loginWithRedirect({
                appState: {
                  returnTo: $path({ route: '/campaigns' }),
                },
              })
            }}
          />
        </div>
      </div>
    </div>
  )
}
